usage = f"** âŒ Wrong Usage âŒ** \n Type `{HNDLR}help spam`"


@Client.on_message(filters.user(SUDO_USERS) & filters.command(["delayspam"], prefixes=HNDLR))
async def delayspam(xspam: Client, e: Message): 
    Rizoel = "".join(e.text.split(maxsplit=1)[1:]).split(" ", 2)
    Rizoelop = Rizoel[1:]
    if len(Rizoelop) == 2:
       counts = int(Rizoelop[0])
       if int(e.chat.id) in GROUP:
            return await e.reply_text("**Sorry !! i Can't Spam Here.**")
       msg = str(Rizoelop[1])
       if re.search(Owners.lower(), msg.lower()):
            return await e.reply_text(usage)("**Sorry !!** I can't Spam On @RiZoeLX's owner")
       sleeptime = float(Rizoel[0])
       if e.reply_to_message:
          reply_to_id = e.reply_to_message.message_id
          for _ in range(counts):
              await xspam.send_message(e.chat.id, msg, reply_to_message_id=reply_to_id)
              await asyncio.sleep(sleeptime)
          return
       for _ in range(counts):
           await xspam.send_message(e.chat.id, msg)
           await asyncio.sleep(sleeptime)
    else:
        await e.reply_text(usage)   
    if LOGS_CHANNEL:
         try:
             await xspam.send_message(LOGS_CHANNEL, f"started Delay Spam By User: {e.from_user.id} \n\n Chat: {e.chat.id} \n Counts: {counts} \n Spam Message: {msg} \n Delay Time: {sleeptime}")
         except Exception as a:
             print(a)
             pass


@Client.on_message(filters.user(SUDO_USERS) & filters.command(["ispam", "inlinespam"], prefixes=HNDLR))
async def ispam(xspam: Client, e: Message):
      Rizoel = "".join(e.text.split(maxsplit=1)[1:]).split(" ", 2)
      if len(Rizoel) == 2:
        counts = int(Rizoel[0])
        if not counts:
          await e.reply_text("Gime spam Counts")
          return
        hm = Rizoel[1]
        if not hm:
          await e.reply_text("you need to specify an user! Reply to any user or gime id/username")
          return
        try:
           user = await xspam.get_users(Rizoel[1])
        except:
           await e.reply_text("**Error:** User not found!")
           return
      elif e.reply_to_message:
        counts = int(Rizoel[0])
        try:
           user = await xspam.get_users(e.reply_to_message.from_user.id)
        except:
           user = e.reply_to_message.from_user 
